sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Define the recipient and CC arrays
    var recipients = [];
    var ccRecipients = [];

    // Get the list of users who have reservations for the room
    var reservationGR = new GlideRecord('ReserveIt Rooms'); 
    reservationGR.addQuery('room', current.sys_id);
    reservationGR.query();
    while (reservationGR.next()) {
        var user = reservationGR.user; 
        if (user) {
            recipients.push(user.email);
        }
    }

    // Get the list of receptionists
    var receptionistGR = new GlideRecord('sys_user');
    receptionistGR.addQuery('roles', 'receptionist'); 
    receptionistGR.query();
    while (receptionistGR.next()) {
        ccRecipients.push(receptionistGR.email);
    }

    // Send email notification
    if (recipients.length > 0) {
        var mail = new GlideEmailOutbound();
        mail.setSubject('Room Details Updated');
        mail.setBody('The details for the room ' + current.name + ' have been updated.');
        mail.setFrom('no-reply@adonis_hotel.com'); // Set the from address
        mail.setTo(recipients.join(','));
        if (ccRecipients.length > 0) {
            mail.setCc(ccRecipients.join(','));
        }
        mail.send();
    }

})(current, previous);
