sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var roomId = g_form.getValue('room');
   var num_days = g_form.getValue('no_of_days');

   var ga = new GlideAjax('ReserveItUtils');
   ga.addParam('sysparm_name', 'getRoomRate');
   ga.addParam('sysparm_roomId', roomId);
   ga.getXMLAnswer(function(response) {
		var total_payment = Number(response) * Number(num_days);
        g_form.setValue('total_payment', String(total_payment));
    });
}
