sys_script_client.script:
function onLoad() {
	// hide status
	g_form.setVisible('status', false);

	if (g_user.hasRole('x_1317506_reserveit.administrator') || g_user.hasRole('x_1317506_reserveit.receptionist')) {
		g_form.setVisible('status', true);
	}

	var currentDate = new Date();
	var formattedDate = formatDate(currentDate);

	g_form.setValue('check_in_date', formattedDate);
	g_form.setValue('check_out_date', formattedDate);
}

function formatDate(date) {
	var year = date.getFullYear();
	var month = ("0" + (date.getMonth() + 1)).slice(-2); // Months are zero-based
	var day = ("0" + date.getDate()).slice(-2);
	return year + "-" + month + "-" + day;
}
