sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	var startDate = g_form.getValue('check_in_date');
	var endDate = newValue;

	if (!startDate) {
		var currentDate = new Date();
		var formattedDate = formatDate(currentDate);
		startDate = formattedDate;
		g_form.setValue('check_in_date', formattedDate);
	}

	var methodName = 'getDateDiff';

	var ga = new GlideAjax('ReserveItUtils');
	ga.addParam('sysparm_name', methodName);
	ga.addParam('sysparm_startDate', startDate);
	ga.addParam('sysparm_endDate', endDate);

	ga.getXMLAnswer(function (response) {
		var numberOfDays = response === '0' ? 1 : response;

		if (numberOfDays < 0) {
			numberOfDays = 1;
			g_form.setValue('check_out_date', startDate);
		}

		g_form.setValue('no_of_days', numberOfDays);

		var roomId = g_form.getValue('room');
		var num_days = g_form.getValue('no_of_days');

		var ga = new GlideAjax('ReserveItUtils');
		ga.addParam('sysparm_name', 'getRoomRate');
		ga.addParam('sysparm_roomId', roomId);
		ga.getXMLAnswer(function (response) {
			var total_payment = Number(response) * Number(num_days);
			g_form.setValue('total_payment', String(total_payment));
		});
	});
}

function formatDate(date) {
	var year = date.getFullYear();
	var month = ("0" + (date.getMonth() + 1)).slice(-2); // Months are zero-based
	var day = ("0" + date.getDate()).slice(-2);
	return year + "-" + month + "-" + day;
}
